import React from 'react';
import './PokemonList.css';  // Assure-toi que le CSS est bien importé

const PokemonList = ({ pokemonData, onSelectPokemon }) => {
  if (!pokemonData || pokemonData.length === 0) {
    return <p>Chargement des Pokémon...</p>;  // Affiche un message pendant le chargement
  }

  return (
    <div className="pokemon-list">
      {pokemonData.map(pokemon => (
        <div
          key={pokemon.id}
          className="pokemon-card"
          onClick={() => onSelectPokemon && onSelectPokemon(pokemon)}
        >
          <img
            src={pokemon.sprite}
            alt={pokemon.name}
          />
          <h3>{pokemon.name}</h3>
        </div>
      ))}
    </div>
  );
};

export default PokemonList;
