import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PokemonCard = ({ pokemon, setSelectedPokemon }) => {
  const [pokemonDetails, setPokemonDetails] = useState(null);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const res = await axios.get(pokemon.url);
        setPokemonDetails(res.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchPokemonDetails();
  }, [pokemon.url]);

  if (!pokemonDetails) return <div>Loading...</div>;

  const pokemonType = pokemonDetails.types[0].type.name;

  return (
    <div
      className="bg-white p-4 rounded-lg m-2 cursor-pointer shadow-lg hover:shadow-xl"
      onClick={() => setSelectedPokemon({ name: pokemon.name, type: pokemonType, image: pokemonDetails.sprites.front_default })}
    >
      <img src={pokemonDetails.sprites.front_default} alt={pokemon.name} className="w-24 h-24" />
      <h3 className="mt-2 text-xl font-bold">{pokemon.name}</h3>
      <p className="text-sm">Type: {pokemonType}</p>
    </div>
  );
};

export default PokemonCard;
