import React, { useState, useEffect } from 'react';
import PokemonList from './components/PokemonList';  // Importation du composant pour afficher la liste des Pokémon

const App = () => {
  const [pokemonData, setPokemonData] = useState([]);

  // Fonction pour récupérer les Pokémon depuis l'API
  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=20');
        const data = await response.json();
        const formattedData = await Promise.all(
          data.results.map(async (pokemon) => {
            const pokemonDetail = await fetch(pokemon.url);
            const pokemonDetailData = await pokemonDetail.json();
            return {
              id: pokemonDetailData.id,
              name: pokemonDetailData.name,
              sprite: pokemonDetailData.sprites.front_default
            };
          })
        );
        setPokemonData(formattedData);
      } catch (error) {
        console.error('Erreur:', error);
      }
    };

    fetchPokemonData();
  }, []);

  return (
    <div className="nintendo-3ds">
      {/* Ecran supérieur (affiche Pokémon sélectionné ou un message) */}
      <div className="top-screen">
        <h2>Sélectionnez un Pokémon</h2>
      </div>

      {/* Ecran inférieur avec la liste des Pokémon */}
      <div className="bottom-screen">
        <PokemonList pokemonData={pokemonData} />
      </div>
    </div>
  );
};

export default App;
