import React, { useEffect, useState } from 'react';
import PokemonList from './components/PokemonList';
import axios from 'axios';
import { motion } from 'framer-motion';
import './styles/3ds-style.css'; // Assure-toi d'importer le fichier CSS

function App() {
  const [pokemonData, setPokemonData] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemons = async () => {
      try {
        const res = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=150');
        setPokemonData(res.data.results);
      } catch (error) {
        console.error(error);
      }
    };

    fetchPokemons();
  }, []);

  return (
    <div className="poke-dex-container">
      {/* Ecran supérieur avec Pokémon sélectionné */}
      <div className="screen upper-screen">
        <motion.div
          className={`background-screen ${selectedPokemon ? selectedPokemon.type : 'normal'}`}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        ></motion.div>
        {selectedPokemon && (
          <div className="pokemon-details">
            <h2 className="pokemon-name">{selectedPokemon.name}</h2>
            <img src={selectedPokemon.image} alt={selectedPokemon.name} className="pokemon-image" />
          </div>
        )}
      </div>

      {/* Ecran inférieur avec liste défilante de Pokémon */}
      <div className="screen lower-screen">
        <PokemonList pokemonData={pokemonData} setSelectedPokemon={setSelectedPokemon} />
      </div>

      {/* Ajout des boutons Nintendo 3DS */}
      <div className="nintendo-buttons">
        <div className="dpad">
          <div></div>
          <div></div>
          <div></div>
          <div></div>
        </div>
        <div className="nintendo-button">A</div>
        <div className="nintendo-button">B</div>
        <div className="nintendo-button">Start</div>
      </div>
    </div>
  );
}

export default App;
