import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PokemonCard = ({ pokemon, setSelectedPokemon }) => {
  const [pokemonDetails, setPokemonDetails] = useState(null);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const res = await axios.get(pokemon.url);
        setPokemonDetails(res.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchPokemonDetails();
  }, [pokemon.url]);

  if (!pokemonDetails) return <div>Loading...</div>;

  const pokemonType = pokemonDetails.types[0].type.name;  // Récupérer le type du Pokémon

  return (
    <div
      className="pokemon-card bg-white p-4 rounded-lg shadow-lg"
      onClick={() => setSelectedPokemon({
        name: pokemon.name,
        type: pokemonType,
        image: pokemonDetails.sprites.front_default
      })}
    >
      <img src={pokemonDetails.sprites.front_default} alt={pokemon.name} />
      <h3>{pokemon.name}</h3>
      <p>Type: {pokemonType}</p>
    </div>
  );
};

export default PokemonCard;
