import React, { useEffect, useState } from 'react';
import PokemonList from './components/PokemonList';
import axios from 'axios';
import { motion } from 'framer-motion';

function App() {
  const [pokemonData, setPokemonData] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemons = async () => {
      try {
        const res = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=150');
        setPokemonData(res.data.results);
      } catch (error) {
        console.error(error);
      }
    };

    fetchPokemons();
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      {/* Background Animation */}
      <motion.div
        className={`absolute top-0 left-0 w-full h-full bg-cover bg-center ${
          selectedPokemon ? `bg-${selectedPokemon.type}-500` : 'bg-blue-500'
        }`}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      ></motion.div>

      {/* Pokemon List */}
      <PokemonList pokemonData={pokemonData} setSelectedPokemon={setSelectedPokemon} />

      {/* Pokemon Details */}
      {selectedPokemon && (
        <div className="relative z-10 mt-8 text-white">
          <h2 className="text-4xl font-bold">{selectedPokemon.name}</h2>
          <img src={selectedPokemon.image} alt={selectedPokemon.name} className="w-48 mt-4" />
        </div>
      )}
    </div>
  );
}

export default App;
